/*  Program to find day of week from given date. 
 */
/* 
    Points to remember:-
        1:- Sunday
        2:- Monday
        3:- Tuesday
        4:- Wednesday
        5:-Thursday
        6:- Friday
        7:- Saturday
 */
#include <bits/stdc++.h>
using namespace std;
int main(){
    int  dd,mm,yy;
    cout << "\n Enter the date(dd/mm/yy):-"; 
    cin >> dd >> mm >> yy;
    cout << dd << mm << yy;
    
    return 0;
}