/* 
    Program to print 1 - 10 using for loop
 */
/*
    Things to remember:-
    for loop syntax:-
        for(initialization;condition;updation){
            statements;
            -----------
        }
 */
#include  <bits/stdc++.h>
using namespace std;
int main(){
    for (int i = 1; i <= 10; i++){
        cout << i << "\n";
    }
        return 0;
}